<script context="module"></script>

<script>import { filterAttrs } from "../helpers.js";
import { setAttrs } from "../helpers.js";
export let attributes = undefined;
export let text = undefined;
export let link = undefined;
export let openLinkInNewTab = undefined;
function mitosis_styling(node, vars) {
    Object.entries(vars || {}).forEach(([p, v]) => {
        if (p.startsWith("--")) {
            node.style.setProperty(p, v);
        }
        else {
            node.style[p] = v;
        }
    });
}
</script>

{#if link}
  <a
    role="button"
    {...filterAttrs(attributes, "on:", false)}
    href={link}
    target={openLinkInNewTab ? "_blank" : undefined}
    use:setAttrs={filterAttrs(attributes, "on:", true)}
  >
    {text}
  </a>
{:else}
  <button
    use:mitosis_styling={attributes.style}
    {...filterAttrs(attributes, "on:", false)}
    class={attributes.class + " button"}
    use:setAttrs={filterAttrs(attributes, "on:", true)}
  >
    {text}
  </button>
{/if}

<style>
  .button {
    all: unset;
  }
</style>