<script context="module" lang="ts">
  export interface TextProps {
    text?: string;
  }
</script>

<script lang="ts">
  export let text: TextProps["text"] = undefined;

  function mitosis_styling(node, vars) {
    Object.entries(vars || {}).forEach(([p, v]) => {
      if (p.startsWith("--")) {
        node.style.setProperty(p, v);
      } else {
        node.style[p] = v;
      }
    });
  }
</script>

<div
  use:mitosis_styling={{
    outline: "none",
  }}
  class="builder-text"
>
  {@html text?.toString() || ""}
</div>